namespace Mild\Database;

class Expression
{
    /**
     * @var string
     */
    protected $value;

    /**
     * Expression constructor.
     * @param string $value
     */
    public function __construct($value)
    {
        $this->value = $value;
    }

    /**
     * @return string
     */
    public function getValue()
    {
        return $this->value;
    }
}